mapper.insert:
insert into shop_order_goods ( order_id,goods_id, number)
values (

    #{orderId},#{item.id},#{item.number}

)

mapper.update:
update shop_goods
set stock = stock - #{number}
where shop_goods.id = #{id}
  and status = 'ok'
  and stock >= #{number}

update shop_order
set express_company = #{expressCompany},
    express_id      = #{expressId},
    status          =#{status}
where id = #{id}

update shop_order
set status = #{status}
where id = #{id}

mapper.delete:
update shop_order
set status = 'deleted'
where id = #{id}
